import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/js/dist/offcanvas';
import 'bootstrap/js/dist/dropdown';
import './Navbar.css';
import logo_light from '../../assets/logo.png';
import logo_dark from '../../assets/logo.png';
import toggle_light from '../../assets/night_icon.png';
import toggle_dark from '../../assets/day_icon.png';
import defaultProfilePic from '../../assets/sample_profile.jpeg'; // Default profile image
import {useDispatch, useSelector} from 'react-redux';
import {useAlert} from 'react-alert';   

const Navbar = ({ theme, setTheme, user, setUser }) => {
    
    const toggleMode = () => {
      setTheme(theme === 'light' ? 'dark' : 'light');
    };
  
    // const alert = useAlert();
    // const dispatch = useDispatch();
    // const{user, loading} = useSelector(state => state.auth);
  
    // const handleSignOut = () => {
    //   setUser(null); // Clear user state
    //   localStorage.removeItem('user'); // Remove user from localStorage
    //   window.location.href = "/login"; // Redirect to login page
    // };
  

    return (
        <nav className={`navbar navbar-expand-lg navbar-${theme} bg-${theme}`}>
            <div className="container-fluid">
                {/* Toggle Button for Responsive Navbar */}
                <button
                    className="navbar-toggler"
                    type="button"
                    data-bs-toggle="offcanvas"
                    data-bs-target="#offcanvasNavbar"
                    aria-controls="offcanvasNavbar"
                >
                    <span className="navbar-toggler-icon"></span>
                </button>

                {/* Logo */}
                <a className="navbar-brand" href="/">
                    <img
                        src={theme === 'light' ? logo_light : logo_dark}
                        alt="Logo"
                        className="logo"
                    />
                </a>

                {/* Offcanvas */}
                <div
                    className="offcanvas offcanvas-end"
                    tabIndex="-1"
                    id="offcanvasNavbar"
                    aria-labelledby="offcanvasNavbarLabel"
                >
                    <div className="offcanvas-header">
                        <h5 className="offcanvas-title" id="offcanvasNavbarLabel">
                            TravelMate
                        </h5>
                        <button
                            type="button"
                            className="btn-close"
                            data-bs-dismiss="offcanvas"
                            aria-label="Close"
                        ></button>
                    </div>
                    <div className="offcanvas-body">
                        {/* Navigation Links */}
                        <ul className="navbar-nav justify-content-end flex-grow-1 pe-3">
                            <li className="nav-item">
                                <a className="nav-link" href="/explore">
                                    Explore
                                </a>
                            </li>
                            <li className="nav-item">
                                <a className="nav-link" href="/about">
                                    About
                                </a>
                            </li>
                            <li className="nav-item">
                                <a className="nav-link" href="/services">
                                    Services
                                </a>
                            </li>
                            <li className="nav-item">
                                <a className="nav-link" href="/contact">
                                    Contact
                                </a>
                            </li>
                        </ul>
                    </div>
                </div>

                {/* Profile or Sign-In Button */}
                
                    <a className="buttn" href="/login">
                        Sign In
                    </a>
                

                {/* Theme Toggle Button */}
                <img
                    onClick={toggleMode}
                    src={theme === 'light' ? toggle_light : toggle_dark}
                    alt="Toggle Theme"
                    className="toggle-icon"
                />
            </div>
        </nav>
    );
};

export default Navbar;
