import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './Navbar.css';
import logo_light from '../../assets/logo.png';
import logo_dark from '../../assets/logo.png';
import toggle_light from '../../assets/night_icon.png';
import toggle_dark from '../../assets/day_icon.png';
import defaultProfilePic from '../../assets/sample_profile.jpeg';

const Navbar = ({ theme, setTheme, user, onSignOut }) => {
  const [dropdownOpen, setDropdownOpen] = useState(false);

  const toggleMode = () => {
    setTheme(theme === 'light' ? 'dark' : 'light');
  };

  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
  };

  return (
    <nav className={`navbar navbar-expand-lg navbar-${theme} bg-${theme}`}>
      <div className="container-fluid">
        <a className="navbar-brand" href="/">
          <img
            src={theme === 'light' ? logo_light : logo_dark}
            alt="Logo"
            className="logo"
          />
        </a>

        <div className="navbar-items ms-auto">
          {user ? (
            <div className="profile-dropdown">
              <img
                src={user.profileImage || defaultProfilePic}
                alt="Profile"
                className="profile-pic"
                onClick={toggleDropdown}
              />
              <span className="profile-name" onClick={toggleDropdown}>
                {user.name}
              </span>
              {dropdownOpen && (
                <div className="dropdown-menu">
                  <a href="/profile" className="dropdown-item">
                    My Profile
                  </a>
                  <button className="dropdown-item" onClick={onSignOut}>
                    Sign Out
                  </button>
                </div>
              )}
            </div>
          ) : (
            <a className="btn btn-primary" href="/login">
              Sign In
            </a>
          )}
        </div>

        <img
          onClick={toggleMode}
          src={theme === 'light' ? toggle_light : toggle_dark}
          alt="Toggle Theme"
          className="toggle-icon"
        />
      </div>
    </nav>
  );
};

export default Navbar;
